import osstr="好今天奇才奇才一"f=open("ST16.bin","rb")for s in str:    f.seek((ord(s)-0x4E00)*32)    buf=f.read(32)    print(s,":")    for i in range(16):        code=0x00        code |= buf[i*2]<<8        code |= buf[i*2+1]        fontmatrix="{:016b}".format(code)        fontmatrix=fontmatrix.replace("0","_")        fontmatrix=fontmatrix.replace("1","X")        print(fontmatrix)