import networkimport machineimport timeimport ntptimetry:  nic = network.WLAN(network.STA_IF) # create station interface  nic.active(True)       # activate the interface  wifi_pass={'fwd-2':'88886666','橙子家':'88884444','fwd':'88886666','028sd.com':'88884444','ZCWH':'00004157'}  wifilist=nic.scan()    for info in wifilist:    print(info)    wifi_name=info[0].decode()    if wifi_name in wifi_pass:      nic.connect(wifi_name,wifi_pass[wifi_name]) # connect to an AP      wificonut=10      while nic.status()==1001 and wificonut>0:        print("连接到：%s=>%s" % (wifi_name,wificonut))        wificonut-=1        time.sleep(1)      if nic.isconnected():        break  print(nic.ifconfig())  ntptime.host="cn.ntppod.com"  ntptime.NTP_DELTA=3155644800  #更新时区+8区  rtc=machine.RTC()  ntptime.settime()  except:  rtc=machine.RTC()finally:  nic.ifconfig()  exec(open('./tft.py').read(),globals())